#!/data/data/com.termux/files/usr/bin/bash
# setup.sh - Tam otomatik kurulum (Termux)
# Kullanƒ±m:
#   chmod +x setup.sh
#   ./setup.sh
# Bu script, gerekli dosyalarƒ± /data/data/com.termux/files/home/discord_snap i√ßerisine yazar.

set -e

WORKDIR="/data/data/com.termux/files/home/discord_snap"
PY_BIN="/data/data/com.termux/files/usr/bin/python"
BASHRC="$HOME/.bashrc"

echo "üì¶ Termux Screenshot Bot - Otomatik Kurulum Ba≈ülƒ±yor"
echo ""

# 1) Paketleri g√ºncelle
echo "üîÑ Paketler g√ºncelleniyor..."
pkg update -y && pkg upgrade -y

# 2) Gerekli paketleri kur
echo "üì• python ve git kuruluyor..."
pkg install -y python git

# 3) Depolama eri≈üimi iste
echo ""
echo "üìÅ Depolama eri≈üimi izni gerekiyor. L√ºtfen gelen izni kabul et."
termux-setup-storage || true
sleep 1

# 4) √áalƒ±≈üma dizinini olu≈ütur
echo "üìÇ √áalƒ±≈üma dizini olu≈üturuluyor: $WORKDIR"
mkdir -p "$WORKDIR"
cd "$WORKDIR"

# 5) requirements.txt olu≈ütur
cat > requirements.txt <<'REQ'
requests==2.31.0
REQ

# 6) discord_screenshot.py olu≈ütur
cat > discord_screenshot.py <<'PY'
#!/usr/bin/env python3
# discord_screenshot.py
# Termux uyumlu: screencap kullanƒ±r, Pillow yok.
# Otomatik olu≈üturuldu.

import os
import time
import json
import requests
import subprocess
from datetime import datetime

# ------- Config y√ºkle -------
SCRIPT_DIR = os.path.dirname(os.path.abspath(__file__))
CONFIG_PATH = os.path.join(SCRIPT_DIR, "config.json")

DEFAULT_CONFIG = {
    "discord_webhook_url": "YOUR_WEBHOOK_URL_HERE",
        # √ñNEMLƒ∞: varsayƒ±lan aralƒ±k 15 dakika (900 saniye).
            "interval_seconds": 900,
                # √ñrnek/alternatif kƒ±sa aralƒ±klar (kullanƒ±lacak olan 'interval_seconds' dir).
                    # Bu alanlar sadece √∂rnek ve referans i√ßindir; aktif aralƒ±k olarak
                        # script 'interval_seconds' deƒüerini kullanƒ±r.
                            "short_interval_seconds_example": 15,
                                "safe_interval_seconds_example": 30,
                                    "base_dir": "/sdcard/snapshots",
                                        "send_startup_notification": True,
                                            "startup_message": "üì¢ Screenshot bot started on device"
                                            }

                                            def load_config():
                                                if not os.path.exists(CONFIG_PATH):
                                                        with open(CONFIG_PATH, "w") as f:
                                                                    json.dump(DEFAULT_CONFIG, f, indent=2)
                                                                            print("‚ö†Ô∏è config.json bulunamadƒ±. √ñrnek olu≈üturuldu:", CONFIG_PATH)
                                                                                    print("L√ºtfen webhook URL'ini config.json i√ßine koy ve scripti tekrar √ßalƒ±≈ütƒ±r.")
                                                                                            raise SystemExit(1)
                                                                                                with open(CONFIG_PATH, "r") as f:
                                                                                                        cfg = json.load(f)
                                                                                                            if not cfg.get("discord_webhook_url") or "YOUR_WEBHOOK_URL_HERE" in cfg.get("discord_webhook_url"):
                                                                                                                    print("‚ö†Ô∏è L√ºtfen config.json i√ßindeki 'discord_webhook_url' alanƒ±nƒ± doldurun.")
                                                                                                                            raise SystemExit(1)
                                                                                                                                return cfg

                                                                                                                                # ------- Cihaz bilgisi alma -------
                                                                                                                                def get_device_info():
                                                                                                                                    # √ñnce Android getprop dene
                                                                                                                                        try:
                                                                                                                                                out = subprocess.check_output(["getprop", "ro.product.model"], stderr=subprocess.DEVNULL).decode().strip()
                                                                                                                                                        if out:
                                                                                                                                                                    return out
                                                                                                                                                                        except Exception:
                                                                                                                                                                                pass
                                                                                                                                                                                    # Fallback: hostname / uname
                                                                                                                                                                                        try:
                                                                                                                                                                                                import platform
                                                                                                                                                                                                        nm = platform.node()
                                                                                                                                                                                                                if nm:
                                                                                                                                                                                                                            return nm
                                                                                                                                                                                                                                except Exception:
                                                                                                                                                                                                                                        pass
                                                                                                                                                                                                                                            return "UnknownDevice"

                                                                                                                                                                                                                                            # ------- Discord g√∂nderimi (basit retry/backoff) -------
                                                                                                                                                                                                                                            def send_file_to_discord(webhook_url, file_path, message):
                                                                                                                                                                                                                                                max_attempts = 3
                                                                                                                                                                                                                                                    delay = 2
                                                                                                                                                                                                                                                        for attempt in range(1, max_attempts + 1):
                                                                                                                                                                                                                                                                try:
                                                                                                                                                                                                                                                                            with open(file_path, "rb") as f:
                                                                                                                                                                                                                                                                                            files = {"file": f}
                                                                                                                                                                                                                                                                                                            data = {"content": message}
                                                                                                                                                                                                                                                                                                                            resp = requests.post(webhook_url, data=data, files=files, timeout=30)
                                                                                                                                                                                                                                                                                                                                        if 200 <= resp.status_code < 300:
                                                                                                                                                                                                                                                                                                                                                        return True, resp.status_code
                                                                                                                                                                                                                                                                                                                                                                    else:
                                                                                                                                                                                                                                                                                                                                                                                    print(f"Discord hata ({resp.status_code}): {resp.text}")
                                                                                                                                                                                                                                                                                                                                                                                            except Exception as e:
                                                                                                                                                                                                                                                                                                                                                                                                        print("Discord g√∂nderim hatasƒ±:", e)
                                                                                                                                                                                                                                                                                                                                                                                                                time.sleep(delay)
                                                                                                                                                                                                                                                                                                                                                                                                                        delay *= 2
                                                                                                                                                                                                                                                                                                                                                                                                                            return False, None

                                                                                                                                                                                                                                                                                                                                                                                                                            def send_text_to_discord(webhook_url, message):
                                                                                                                                                                                                                                                                                                                                                                                                                                try:
                                                                                                                                                                                                                                                                                                                                                                                                                                        resp = requests.post(webhook_url, json={"content": message}, timeout=15)
                                                                                                                                                                                                                                                                                                                                                                                                                                                if 200 <= resp.status_code < 300:
                                                                                                                                                                                                                                                                                                                                                                                                                                                            return True
                                                                                                                                                                                                                                                                                                                                                                                                                                                                except Exception as e:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                        print("Discord text g√∂nderim hatasƒ±:", e)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            return False

                                                                                                                                                                                                                                                                                                                                                                                                                                                                            # ------- Screencap ve kaydetme -------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            def create_day_folder(base_dir):
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                today = datetime.now().strftime("%d_%m_%Y")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    day_folder = os.path.join(base_dir, today)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        os.makedirs(day_folder, exist_ok=True)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            return day_folder

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            def take_screenshot(target_path):
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                """Android screencap -p target_path"""
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    try:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            subprocess.run(["/system/bin/screencap", "-p", target_path], check=True)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                except FileNotFoundError:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        try:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    subprocess.run(["screencap", "-p", target_path], check=True)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            except Exception as e:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        print("Ekran g√∂r√ºnt√ºs√º komutu bulunamadƒ± veya √ßalƒ±≈ütƒ±rƒ±lamadƒ±:", e)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    return False
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        except subprocess.CalledProcessError as e:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("Ekran g√∂r√ºnt√ºs√º alƒ±namadƒ±:", e)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        return False
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            return True

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            # ------- Single-instance kontrol -------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            def already_running():
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                try:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        out = subprocess.run(["pgrep", "-f", "discord_screenshot.py"], capture_output=True, text=True)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                lines = [l for l in out.stdout.splitlines() if l.strip()]
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        # Eƒüer 1 veya daha fazla pid varsa, script zaten √ßalƒ±≈üƒ±yor olabilir
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                # (Basit kontrol ‚Äî aynƒ± scriptin birden fazla √∂rneƒüini engeller)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if len(lines) >= 1:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    return True
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        except Exception:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                pass
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    return False

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    # ------- Main -------
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    def main():
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if already_running():
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("‚ö†Ô∏è G√∂r√ºn√º≈üe g√∂re script zaten √ßalƒ±≈üƒ±yor. Yeni bir √∂rnek ba≈ülatƒ±lmayacak.")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        return

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            cfg = load_config()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                webhook = cfg["discord_webhook_url"]
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    base_dir = cfg.get("base_dir", DEFAULT_CONFIG["base_dir"])
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        interval = int(cfg.get("interval_seconds", DEFAULT_CONFIG["interval_seconds"]))

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            os.makedirs(base_dir, exist_ok=True)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                device_info = get_device_info()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    print("üöÄ Bot ba≈ülƒ±yor. Kayƒ±t dizini:", base_dir)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        print("üì± Device info:", device_info)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print("‚è± Interval (s):", interval)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    # Ba≈ülangƒ±√ß bildirimi
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if cfg.get("send_startup_notification", True):
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                startup_msg = cfg.get("startup_message", DEFAULT_CONFIG["startup_message"])
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        timestamp = datetime.now().strftime('%d-%m-%Y %H:%M:%S')
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                composed = f"{startup_msg} ‚Äî {device_info} ‚Äî {timestamp}"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        try:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ok = send_text_to_discord(webhook, composed)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if ok:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("‚úÖ Ba≈ülangƒ±√ß bildirimi Discord'a g√∂nderildi.")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            else:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print("‚ö†Ô∏è Ba≈ülangƒ±√ß bildirimi g√∂nderilemedi.")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    except Exception as e:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("Ba≈ülangƒ±√ß bildirimi sƒ±rasƒ±nda hata:", e)

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    # D√∂ng√º
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        while True:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                try:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            day_folder = create_day_folder(base_dir)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        file_name_time = datetime.now().strftime("shot_%H_%M_%S.png")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    target_path = os.path.join(day_folder, file_name_time)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ok = take_screenshot(target_path)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if ok:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            print(f"üì∏ Kaydedildi: {target_path}")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            message = f"Snapped on {datetime.now().strftime('%d-%m-%Y %H:%M:%S')} ‚Äî {device_info}"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            sent, status = send_file_to_discord(webhook, target_path, message)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if sent:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("üì§ Discord'a g√∂nderildi.")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                else:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    print("‚ö†Ô∏è Discord'a g√∂nderilemedi (en son deneme ba≈üarƒ±sƒ±z).")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                else:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                print("‚ö†Ô∏è Ekran g√∂r√ºnt√ºs√º alƒ±namadƒ±; interval sonunda yeniden denenecek.")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        except Exception as e:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    print("Genel hata d√∂ng√ºde:", e)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            time.sleep(interval)

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if __name__ == "__main__":
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                main()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                PY

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                # 7) config.json template (varsayƒ±lan aralƒ±k 900s = 15 dakika; kƒ±sa/orta √∂rnek alanlarƒ± referans i√ßin eklendi)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                cat > config.json <<'CFG'
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  "discord_webhook_url": "YOUR_WEBHOOK_URL_HERE",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    "base_dir": "/sdcard/snapshots",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      "interval_seconds": 900,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        "short_interval_seconds_example": 15,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          "safe_interval_seconds_example": 30,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            "send_startup_notification": true,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              "startup_message": "üì¢ Screenshot bot started on device"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              CFG

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              # 8) pip kurulumu ve baƒüƒ±mlƒ±lƒ±klar
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              echo "üì¶ Python paketleri y√ºkleniyor..."
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              pip install --upgrade pip
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              pip install -r requirements.txt

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              # 9) √áalƒ±≈ütƒ±rƒ±labilir izinler
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              chmod +x discord_screenshot.py

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              # 10) .bashrc'ye auto-start ekle (aynƒ± satƒ±r birden fazla eklenmesin)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              AUTOSTART_CMD="pgrep -f discord_screenshot.py > /dev/null || nohup python $WORKDIR/discord_screenshot.py > /dev/null 2>&1 &"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              if ! grep -Fxq "$AUTOSTART_CMD" "$BASHRC"; then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                echo "" >> "$BASHRC"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  echo "# Start screenshot bot on Termux launch" >> "$BASHRC"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    echo "$AUTOSTART_CMD" >> "$BASHRC"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      echo "‚úÖ Otomatik ba≈ülatma satƒ±rƒ± ~/.bashrc'ye eklendi."
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      else
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        echo "‚ÑπÔ∏è Otomatik ba≈ülatma zaten ~/.bashrc i√ßinde."
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        fi

